def a = (int)(Math.random() * 1000)
def b = (int)(Math.random() * 1000)

testRunner.testCase.setPropertyValue( "addArg1", String.valueOf(a) )
testRunner.testCase.setPropertyValue( "addArg2", String.valueOf(b) )

con:configuration.scriptText:
import com.eviware.soapui.support.XmlHolder

def holder = new XmlHolder( messageExchange.responseContentAsXml )
def responseXml = holder.getPrettyXml()

def sum = Integer.parseInt(holder.getNodeValue('//*:AddResult'))
def a = Integer.parseInt(context.expand('${#TestCase#addArg1}'))
def b = Integer.parseInt(context.expand('${#TestCase#addArg2}'))

def expected = a + b

assert sum == expected

